script:
frappe.ui.form.on('Driver', {
	employee: function(frm) {
		if (frm.doc.employee) {
			// Fetch employee name from Employee doctype
			frappe.db.get_value('Employee', frm.doc.employee, 'employee_name', (r) => {
				if (r && r.employee_name) {
					frm.set_value('full_name', r.employee_name);
				}
			});
		} else {
			// Clear full_name if employee is cleared
			if (!frm.doc.full_name || frm.doc.full_name === '') {
				// Only clear if it's empty to avoid overwriting manually entered names
				frm.set_value('full_name', '');
			}
		}
	}
});
